sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {
    var x = current.subject3;
    var y = current.fac3_opt;
    var s = current.getDisplayValue('number');
    var num = current.num3_class;
    var se = s;
    var sc = 0;
    var flag = [0, 0, 0, 0, 0, 0];
    while (sc < num) {
        var z = Math.floor(Math.random() * (6)) + 1;
        if (z == 1 && flag[0] == 0) {
            var n = getDay(x, y, se, 'monday');
            if (n == 0) {
                sc++;
                flag[0] = 1;
            }
        } else if (z == 2 && flag[1] == 0) {
            var n1 = getDay(x, y, se, 'tuesday');
            if (n1 == 0) {
                sc++;
                flag[1] = 1;
            }
        } else if (z == 3 && flag[2] == 0) {
            var n2 = getDay(x, y, se, 'wednesday');
            if (n2 == 0) {
                sc++;
                flag[2] = 1;
            }
        } else if (z == 4 && flag[3] == 0) {
            var n3 = getDay(x, y, se, 'thursday');
            if (n3 == 0) {
                sc++;
                flag[3] = 1;
            }
        } else if (z == 5 && flag[4] == 0) {
            var n4 = getDay(x, y, se, 'friday');
            if (n4 == 0) {
                sc++;
                flag[4] = 1;
            }
        } else if (z == 6 && flag[5] == 0) {
            var n5 = getDay(x, y, se, 'saturday');
            if (n5 == 0) {
                sc++;
                flag[5] = 1;
            }

        }
    }
})(current, previous);
