sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }
    var y;
    var s = g_form.getValue('subject3');
    var k = new GlideAjax('getFaculty');
    k.addParam('sysparm_name', 'CourseSysId');
    k.addParam('cour', s);
    k.getXML(getz);

    function getz(response) {
        y = response.responseXML.documentElement.getAttribute('answer');
        var kn = new GlideAjax('getFaculty');
        kn.addParam('sysparm_name', 'Faculty');
        kn.addParam('sub_opt', y);
        kn.getXML(getf);

        function getf(response) {
            var e = response.responseXML.documentElement.getAttribute('answer');
            var fields = [];
            fields = JSON.parse(e);
            for (var count = 0; count < fields.length; count++) {
                g_form.addOption('fac3_opt', fields[count], fields[count]);
            }
        }
    }

    //Type appropriate comment here, and begin script below

}
