function onLoad() {
    if (g_form.getValue('fac1_opt') != '') {
		g_form.setReadOnly('stream',true);
		g_form.setReadOnly('number',true);
        g_form.setReadOnly('subject1', true);
        g_form.setReadOnly('fac1_opt', true);
        g_form.setReadOnly('n1_class', true);
        g_form.setReadOnly('insert_sub1', true);
    }
    if (g_form.getValue('fac2_opt') != '') {
        g_form.setReadOnly('subject2', true);
        g_form.setReadOnly('fac2_opt', true);
        g_form.setReadOnly('num2_class', true);
        g_form.setReadOnly('insert_sub2', true);
    }
    if (g_form.getValue('fac3_opt') != '') {
        g_form.setReadOnly('subject3', true);
        g_form.setReadOnly('fac3_opt', true);
        g_form.setReadOnly('num3_class', true);
        g_form.setReadOnly('insert_sub3', true);
    }
    if (g_form.getValue('fac4_opt') != '') {
        g_form.setReadOnly('subject4', true);
        g_form.setReadOnly('fac4_opt', true);
        g_form.setReadOnly('num4_class', true);
        g_form.setReadOnly('insert_sub4', true);
    }
    if (g_form.getValue('fac5_opt') != '') {
        g_form.setReadOnly('subject5', true);
        g_form.setReadOnly('fac5_opt', true);
        g_form.setReadOnly('num5_class', true);
        g_form.setReadOnly('insert_sub5', true);
    }
    if (g_form.getValue('fac6_opt') != '') {
        g_form.setReadOnly('subject6', true);
        g_form.setReadOnly('fac6_opt', true);
        g_form.setReadOnly('num6_class', true);
        g_form.setReadOnly('insert_sub6', true);
    }
}
