function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }
    var str = g_form.getValue('stream');
    g_form.clearOptions('fac1_opt');
    var s = g_form.getValue('subject1');
    var kn = new GlideAjax('getFaculty');
    kn.addParam('sysparm_name', 'Faculty');
    kn.addParam('sub_opt', s);
    kn.getXML(getf);

    function getf(response) {
        var e = response.responseXML.documentElement.getAttribute('answer');
        var fields = [];
        fields = JSON.parse(e);
        for (var count = 0; count < fields.length; count++) {
            g_form.addOption('fac1_opt', fields[count], fields[count]);
        }
    }
    /*var k = new GlideAjax('getSubject2');
    k.addParam('sysparm_name', 'Subject2');
    k.addParam('sub_opt', s);
    k.addParam('sr', str);
    /*
        k.getXML(geth);/
