sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }
	g_form.clearOptions('fac2_opt');
    var s = g_form.getValue('subject2');
    var kn = new GlideAjax('getFaculty');
    kn.addParam('sysparm_name', 'Faculty');
    kn.addParam('sub_opt', s);
    kn.getXML(getf);

    function getf(response) {
        var e = response.responseXML.documentElement.getAttribute('answer');
        var fields = [];
        fields = JSON.parse(e);
        for (var count = 0; count < fields.length; count++) {
            g_form.addOption('fac2_opt', fields[count], fields[count]);
        }
    }
	//g_form.setReadOnly('subject2', true);
}
