sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }
	g_form.setReadOnly('stream',true);
    var str = g_form.getValue('stream');
    try {
        var k = new GlideAjax('getCourses');
        k.addParam('sysparm_name', 'Courses');
        k.addParam('stream', str);
        k.getXML(getc);

        function getc(response) {
            var y = response.responseXML.documentElement.getAttribute('answer');
            var fields = [];
            fields = JSON.parse(y);
            var i = 1;
            var cc = fields.length;
            for (var count = 0; count < cc; count++) {
                g_form.setValue('subject' + i, fields[count]);
                g_form.setReadOnly('subject' + i, true);
                i++;
            }
        }
	}catch (e) {

        }

        //Type appropriate comment here, and begin script below

    }
