sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }
	var str = g_form.getValue('stream');
    var srm = g_form.getDisplayBox('stream').value;
    var num;
    var k = new GlideAjax('getSectionNumber');
    k.addParam('sysparm_name', 'SectionNumber');
    k.addParam('stream', str);
    k.getXML(getc);

    function getc(response) {
        var y = response.responseXML.documentElement.getAttribute('answer');
        if (srm == 'Civil Engineering')
            num = 'CE00' + ++y;
        else if (srm == 'Bio Technology')
            num = 'BT00' + ++y;
        else if (srm == 'Computer Science and Engineering')
            num = 'CSE00' + ++y;
        else if (srm == 'Electronics and Communication Engineering')
            num = 'ECE00' + ++y;
        else if (srm == 'Mechanical Engineering')
            num = 'ME00' + ++y;
        g_form.setValue('number', num);
        g_form.setReadOnly('number', true);
    }

   //Type appropriate comment here, and begin script below

}
