sys_script_include.script:
var getFaculty = Class.create();
getFaculty.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
    CourseSysId: function() {
		var s=this.getParameter('cour');
        var gr = new GlideRecord('x_331786_proj_tt_courses');
        gr.addQuery('course_name', s);
        gr.query();
        while (gr.next()) {
            var sec = gr.sys_id;
        }
		return sec;
    },
    Faculty: function() {
        var sc = this.getParameter('sub_opt');
        var faculty = [];
        var jsonSub;
        var gr = new GlideRecord('x_331786_proj_tt_faculty');
        gr.addQuery('sub_opt', sc);
        gr.query();
        while (gr.next()) {
            faculty.push(gr.fac_name.toString());
            jsonSub = JSON.stringify(faculty);
        }
        return jsonSub;
    },

    type: 'getFaculty'
});
